mapper.update:
update coach_basic_info set validation = 0 
where coach_id in 

		 #{item}

mapper.select:
select coach_id, coach_name, identity_card_no, phone, C.school_id, register_date, training_place, login_pwd, C.validation, teaching_age, service_count, star, online, IF((PHOTO IS NULL), 0, 1) AS havePhoto , school_name from coach_basic_info as C join school_info as S on C.school_id = S.school_id
C.validation = 1 and S.validation = 1
and coach_id = #{coach_id}
and coach_name like "%"#{coach_name}"%"
and identity_card_no like "%"#{identity_card_no}"%"
and phone like "%"#{phone}"%"
and photo like "%"#{photo}"%"
and school_id = #{school_id}
and register_date like "%"#{register_date}"%"
and training_place like "%"#{training_place}"%"
and contract_no like "%"#{contract_no}"%"
and login_pwd like "%"#{login_pwd}"%"
and validation = #{validation}
and teaching_age = #{teaching_age}
and service_count = #{service_count}
and star = #{star}
and online = #{online}

select coach_id, coach_name, identity_card_no, phone, C.school_id, register_date, training_place, login_pwd, C.validation, teaching_age, service_count, star, online, IF((PHOTO IS NULL), 0, 1) AS havePhoto school_name from coach_basic_info as C join school_info as S on C.school_id = S.school_id
C.validation = 1 and S.validation = 1
and coach_id = #{coach_id}
and coach_name like "%"#{coach_name}"%"
and identity_card_no like "%"#{identity_card_no}"%"
and phone like "%"#{phone}"%"
and photo like "%"#{photo}"%"
and school_id = #{school_id}
and register_date like "%"#{register_date}"%"
and training_place like "%"#{training_place}"%"
and contract_no like "%"#{contract_no}"%"
and login_pwd like "%"#{login_pwd}"%"
and validation = #{validation}
and teaching_age = #{teaching_age}
and service_count = #{service_count}
and star = #{star}
and online = #{online}
order by #{sort} #{order}
LIMIt #{page}, #{rows}

