mapper.update:
update evaluation_record set validation = 0 
where evaluation_record_id in 

		 #{item}

mapper.select:
select 
	E.evaluation_time,
	C.coach_name,
	C.coach_id,
	C.phone,
	O.course_status,
	O.order_id,
	E.evaluation,
	E.evaluation_type,
	E.evaluation_record_id
from 
	evaluation_record as E
join
	coach_basic_info as C
on 
	E.coach_id = C.coach_id
join 
	order_record as O
on 
	E.order_id = O.order_id


		and evaluation_record_id = #{evaluation_record_id}


		and E.student_id = #{student_id}


		and E.coach_id = #{coach_id}


		and E.order_id = #{order_id}


		and content like "%"#{content}"%"


		and skill = #{skill}


		and service_attitude = #{service_attitude}


		and hygiene = #{hygiene}


		and evaluation_time like "%"#{evaluation_time}"%"


		and evaluation_type = #{evaluation_type}


		and evaluation = #{evaluation}


		and validation = #{validation}

select 
	E.evaluation_time,
	C.coach_name,
	C.coach_id,
	C.phone,
	O.course_status,
	O.order_id,
	E.evaluation,
	E.evaluation_type,
	E.evaluation_record_id
from 
	evaluation_record as E
join
	coach_basic_info as C
on 
	E.coach_id = C.coach_id
join 
	order_record as O
on 
	E.order_id = O.order_id


		and evaluation_record_id = #{evaluation_record_id}


		and E.student_id = #{student_id}


		and E.coach_id = #{coach_id}


		and E.order_id = #{order_id}


		and content like "%"#{content}"%"


		and skill = #{skill}


		and service_attitude = #{service_attitude}


		and hygiene = #{hygiene}


		and evaluation_time like "%"#{evaluation_time}"%"


		and evaluation_type = #{evaluation_type}


		and validation = #{validation}



	order by ${sort} ${order}


	LIMIt #{page}, #{rows}

select 
	count(evaluation_record_id)
from 
	evaluation_record as E
join
	coach_basic_info as C
on 
	E.coach_id = C.coach_id
join 
	order_record as O
on 
	E.order_id = O.order_id


		and E.evaluation_record_id = #{evaluation_record_id}


		and E.student_id = #{student_id}


		and E.coach_id = #{coach_id}


		and E.order_id = #{order_id}


		and content like "%"#{content}"%"


		and skill = #{skill}


		and service_attitude = #{service_attitude}


		and hygiene = #{hygiene}


		and evaluation_time like "%"#{evaluation_time}"%"


		and evaluation_type = #{evaluation_type}


		and validation = #{validation}

select E.evaluation_time, C.coach_name, C.coach_id, O.course_status, O.order_id, O.student_level, E.evaluation, E.evaluation_type, E.evaluation_record_id, SB.student_name, SB.student_phone, SC.school_type, SB.student_id, SC.school_id from evaluation_record as E join order_record as O on E.order_id = O.order_id join coach_basic_info as C on O.coach_id = C.coach_id join student_basic_info SB on O.student_id = SB.student_id join school_info as SC on O.school_id = SC.school_id
and O.student_id = #{student_id}
and O.coach_id = #{coach_id}
and E.evaluation = #{evaluation}
and E.evaluation_type = #{evaluation_type}
E.order_id = #{order_id}
order by ${sort} ${order}
limit #{page}, #{rows}

select count(*) from evaluation_record as E join order_record as O on E.order_id = O.order_id join coach_basic_info as C on O.coach_id = C.coach_id join student_basic_info as SB on O.student_id = SB.student_id join school_info as SC on O.school_id = SC.school_id
and O.student_id = #{student_id}
and O.coach_id = #{coach_id}
and E.evaluation = #{evaluation}
and E.evaluation_type = #{evaluation_type}
E.order_id = #{order_id}

select E.evaluation_time, C.coach_name, C.coach_id, O.course_status, O.order_id, E.evaluation, E.evaluation_type, E.evaluation_record_id, SB.student_name, SC.school_type, SB.student_id, SC.school_id, SC.school_name from evaluation_record as E join coach_basic_info as C on E.coach_id = C.coach_id join order_record as O on E.order_id = O.order_id join student_basic_info SB on E.student_id = SB.student_id join school_info as SC on O.school_id = SC.school_id
or E.evaluation_time like "%"#{evaluation_time}"%"
or SB.student_name like "%"#{search}"%" or C.coach_name like "%"#{search}"%" or SC.school_type like "%"#{search}"%" or O.course_status like "%"#{search}"%" or E.evaluation like "%"#{search}"%" or E.evaluation_type like "%"#{search}"%"
order by ${sort} ${order}
limit #{page}, #{rows}

