mapper.select:
select R.order_id, R.student_id, R.coach_id, R.school_id, R.course_status, R.order_time, R.order_amount, R.order_status, R.training_start_time, R.training_end_time, R.order_dead_time, R.order_memo, coach.coach_name, coach.phone, S.school_address, IF((coach.photo IS NULL), 0, 1) AS havePhoto from order_record as R join coach_basic_info as coach on R.coach_id = coach.coach_id join school_info as S on R.school_id = S.school_id
R.validation = 1 and coach.validation = 1 and S.validation = 1
and order_id = #{order_id}
and student_id = #{student_id}
and R.coach_id = #{coach_id}
and school_id = #{school_id}
and order_time like "%"#{order_time}"%"
and order_amount = #{order_amount}
and order_status = #{order_status}
and training_start_time like "%"#{training_start_time}"%"
and training_end_time like "%"#{training_end_time}"%"
and order_dead_time like "%"#{order_dead_time}"%"
and order_memo like "%"#{order_memo}"%"
and validation = #{validation}
and course_status = #{course_status}
order by R.training_start_time DESC

select R.order_id, R.student_id, R.coach_id, R.school_id, R.course_status, R.order_time, R.order_amount, R.order_status, R.training_start_time, R.training_end_time, R.order_dead_time, R.order_memo, coach.coach_name, coach.phone, coach.training_place, IF((coach.photo IS NULL), 0, 1) AS havePhoto from order_record as R join coach_basic_info as coach on R.coach_id = coach.coach_id
R.validation = 1 and coach.validation = 1
and order_id = #{order_id}
and student_id = #{student_id}
and R.coach_id = #{coach_id}
and school_id = #{school_id}
and order_time like "%"#{order_time}"%"
and order_amount = #{order_amount}
and order_status = #{order_status}
and training_start_time like "%"#{training_start_time}"%"
and training_end_time like "%"#{training_end_time}"%"
and order_dead_time like "%"#{order_dead_time}"%"
and order_memo like "%"#{order_memo}"%"
and validation = #{validation}
and course_status = #{course_status}
order by #{sort} #{order}
LIMIt #{page}, #{rows}

select 
count(R.order_id) 
from 
	order_record as R 
	join 
	coach_basic_info as coach 
	on 
	R.coach_id = coach.coach_id

	R.validation = 1
	and coach.validation = 1

		and order_id = #{order_id}


		and student_id = #{student_id}


		and coach_id = #{coach_id}


		and school_id = #{school_id}


		and order_time like "%"#{order_time}"%"


		and order_amount = #{order_amount}


		and order_status = #{order_status}


		and training_start_time like "%"#{training_start_time}"%"


		and training_end_time like "%"#{training_end_time}"%"


		and order_dead_time like "%"#{order_dead_time}"%"


		and order_memo like "%"#{order_memo}"%"


		and validation = #{validation}


		and course_status = #{course_status}

select S.student_name, S.student_phone, S.student_id, O.coach_id, O.order_id, O.school_id, O.course_status, O.order_time, O.training_start_time, O.training_end_time, O.order_status, DATE_FORMAT(now(), '%Y-%m-%d %H:%i:%S') as queryTime from order_record as O join student_basic_info as S on O.student_id = S.student_id
O.coach_id = #{coach_id} and S.validation = 1
and order_status = #{order_status}
order by ${sort} ${order}
limit #{page}, #{rows}

select O.order_id, O.training_start_time, O.order_status, O.order_amount, O.order_dead_time, O.order_time, S.student_id, S.student_name, C.coach_id, C.coach_name, C.phone, C.star, SC.school_id from order_record as O join student_basic_info as S on O.student_id = S.student_id join coach_basic_info as C on O.coach_id = C.coach_id join school_info as SC on O.school_id = SC.school_id
O.student_id = #{student_id} and O.student_id = S.student_id and O.coach_id = C.coach_id and O.school_id = SC.school_id and O.validation = 1
order by ${sort} ${order}
limit #{page}, #{rows}

select O.training_start_time, O.training_end_time, O.order_id, O.order_amount, O.course_status, O.order_status, O.student_id, O.operation, SB.student_name, SB.student_phone, C.coach_id, C.coach_name, C.phone as coach_phone, SC.school_id,getCoachTeachRecord SC.school_name, SB.student_level from order_record as O join student_basic_info as SB on O.student_id = SB.student_id join coach_basic_info as C on O.coach_id = C.coach_id join school_info as SC on C.school_id = SC.school_id
where 1=1
and order_status = #{order_status}
and training_start_time <= #{now} and training_end_time >= #{now} and scanned = 1
and training_start_time > #{now} and scanned = 0
and( SB.student_name like "%"#{search}"%" or SB.student_phone like "%"#{search}"%" or C.coach_name like "%"#{search}"%" or SC.school_name like "%"#{search}"%")
order by ${sort} ${order}
limit #{page}, #{rows}

