select SD.deposit_record_id, SD.deposit_amount, SD.deposit_way, SD.deposit_date, SD.deposit_type, SB.student_name, SB.student_phone, SD.remark from student_deposit_record as SD join student_basic_info as SB on SD.student_id = SB.student_id
order by ${sort} ${order}
limit #{page}, #{rows}

